import React from 'react';
import '../styleesheets/Cliente.css';
import ListadoClientes from './clientes/ListadoClientes';
import RegistrarClientes from './clientes/RegistrarCliente';
import { useState, useEffect } from "react";  
import { Modal } from 'react-bootstrap';  
import Axios from "axios";

function Clientes() {
    const [show, setShow] = useState(false);
    const [clientesList, setClientes] = useState([]);
    const token = localStorage.getItem('token');

    const listarClientes = async() =>{
        await Axios.get("http://localhost:8010/api/clientes",{headers: {'Authorization': token,},})
        .then((response) => {setClientes(response.data);})
        .catch((error) => {console.error("Hubo un error al obtener la lista de clientes:", error.response);});
    };

    useEffect(() => {
        listarClientes();
    }, []);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <main>
            <div className='row'>
                <div className='col-sm-12 col-md-12 col-lg-12'>
                    <div className='card'>
                        <div className='card-header'>
                            <div className='row'>
                                <div className='col-sm-10 col-md-10 col-lg-10'>
                                    <h3><span className='fa fa-address-book'></span>&nbsp;Listado Clientes</h3>
                                </div>
                                <div className='col-sm-2 col-md-2 col-lg-2'>
                                    <button onClick={handleShow} className='btn-agregar'><span className='fa fa-plus-square'></span>&nbsp;Nuevo</button>
                                </div>
                            </div>
                        </div>
                        <div className='card-body'>
                            <div className="row">
                                <div className="col-sm-12 col-md-12 col-lg-12">
                                    <ListadoClientes clientesList={clientesList} />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <Modal show={show} onHide={handleClose} style={{ maxWidth: '100%' }}>
            <Modal.Header closeButton>
                </Modal.Header>
                <Modal.Body>
                    <RegistrarClientes listarClientes={listarClientes} />
                </Modal.Body>
            </Modal>
        </main>
    );
}
export default Clientes;
